/* tạo phòng ban */
CREATE TABLE HAUI_2021600453_LamNguyenHong.Department (
  DepartmentId char(36) NOT NULL COMMENT 'khóa chính',
  DepartmentCode varchar(20) NOT NULL COMMENT 'mã phòng ban',
  DepartmentName varchar(255) NOT NULL COMMENT 'tên phòng ban',
  CreatedDate datetime DEFAULT NULL COMMENT 'ngày tạo',
  CreatedBy varchar(255) DEFAULT NULL COMMENT 'người tạo',
  ModifiedDate datetime DEFAULT NULL COMMENT 'ngày sừa gần nhất',
  ModifiedBy varchar(255) DEFAULT NULL COMMENT 'người sửa gần nhất',
  PRIMARY KEY (DepartmentId)
)
ENGINE = INNODB,
AVG_ROW_LENGTH = 1820,
CHARACTER SET utf8mb4,
COLLATE utf8mb4_0900_ai_ci,
COMMENT = 'danh muc phong ban';

/* tạo vị trí */
CREATE TABLE HAUI_2021600453_LamNguyenHong.Positions (
  PositionId char(36) NOT NULL COMMENT 'khóa chính',
  PositionCode varchar(20) NOT NULL COMMENT 'mã vị trí',
  PositionName varchar(255) DEFAULT NULL COMMENT 'tên vị trí',
  CreatedDate datetime DEFAULT NULL COMMENT 'ngay tao ban ghi',
  CreatedBy varchar(255) DEFAULT NULL COMMENT 'nguoi tao',
  ModifiedDate datetime DEFAULT NULL COMMENT 'ngay gio sua ban ghi gan nhat',
  ModifiedBy varchar(255) DEFAULT NULL COMMENT 'nguoi chinh sua gan nhat',
  PRIMARY KEY (PositionId)
)
ENGINE = INNODB,
AVG_ROW_LENGTH = 1820,
CHARACTER SET utf8mb4,
COLLATE utf8mb4_0900_ai_ci,
COMMENT = 'danh muc vi tri chuc vu';

/* tạo nhân viên */
CREATE TABLE HAUI_2021600453_LamNguyenHong.Employee (
  EmployeeId char(36) NOT NULL COMMENT 'khóa chính',
  EmployeeCode varchar(20) NOT NULL COMMENT 'mã nv',
  FullName varchar(100) NOT NULL COMMENT 'họ tên',
  DateOfBirth date DEFAULT NULL COMMENT 'ngày sinh',
  Gender int DEFAULT NULL COMMENT 'giói tính (0-nam, 1-nữ,2-không xác định)',
  IdentityNumber varchar(25) NOT NULL COMMENT 'cố cccd',
  IdentityDate date DEFAULT NULL COMMENT 'ngày cấp',
  IdentityPlace varchar(255) DEFAULT NULL COMMENT 'nơi cấp',
  Email varchar(100) NOT NULL COMMENT 'email',
  PhoneNumber varchar(50) NOT NULL COMMENT 'số đt di động',
  Address varchar(255) DEFAULT NULL COMMENT 'địa chỉ',
  LandlineNumber varchar(50) DEFAULT NULL COMMENT 'số đt cố định',
  BankAccount varchar(25) DEFAULT NULL COMMENT 'tài khoản ngân hàng',
  BankName varchar(255) DEFAULT NULL COMMENT 'tên ngân hàng',
  Branch varchar(255) DEFAULT NULL COMMENT 'chi nhánh',
  PositionId char(36) DEFAULT NULL COMMENT 'vị trí',
  DepartmentId char(36) DEFAULT NULL COMMENT 'phòng ban',
  CreatedDate date DEFAULT NULL COMMENT 'ngày tạo',
  CreatedBy varchar(255) DEFAULT NULL COMMENT 'người tạo',
  ModifiedDate date DEFAULT NULL COMMENT 'ngày sửa gần nhất',
  ModifiedBy varchar(255) DEFAULT NULL COMMENT 'người sửa gần nhất',
  PRIMARY KEY (EmployeeId)
)
ENGINE = INNODB,
AVG_ROW_LENGTH = 1638,
CHARACTER SET utf8mb4,
COLLATE utf8mb4_0900_ai_ci,
COMMENT = 'danh muc nhan vien';

ALTER TABLE HAUI_2021600453_LamNguyenHong.Employee
ADD UNIQUE INDEX UK_Employee_EmployeeCode (EmployeeCode);

ALTER TABLE HAUI_2021600453_LamNguyenHong.Employee
ADD CONSTRAINT FK_Employee_Department FOREIGN KEY (DepartmentId)
REFERENCES HAUI_2021600453_LamNguyenHong.Department (DepartmentId);

ALTER TABLE HAUI_2021600453_LamNguyenHong.Employee
ADD CONSTRAINT FK_Employee_Position FOREIGN KEY (PositionId)
REFERENCES HAUI_2021600453_LamNguyenHong.Positions (PositionId);


